mapper.insert:
/*HomeTaxMapper.createTax*/ INSERT INTO HOMETAX ( SEQ, MYID, DAY, WATER, ELEC, GAS, INTER, MANAGERFEE, MONTHFEE, TOTALFEE, RESTFEE, INPUTFEE, DEL
,HTCUSTOME
) VALUES( SEQ_HOMETAX.NEXTVAL, #{myid}, #{day}, #{water}, #{elec}, #{gas}, #{inter}, #{managerfee}, #{monthfee}, #{totalfee}, #{restfee}, 0, 0
,#{HTCUSTOME, jdbcType=VARCHAR}
)

mapper.select:
/*HomeTaxMapper.detailTax*/ SELECT H.MYID, H.DAY, H.WATER, H.ELEC, H.GAS, H.INTER, H.MANAGERFEE, H.MONTHFEE, H.TOTALFEE, H.RESTFEE, H.INPUTFEE, H.DEL, M.NAME FROM HOMETAX H , HOME_MEMBER M WHERE H.MYID = M.MYID
AND H.MYID in (select myid from home_member where auth = '1')
AND H.MYID = #{myid}
AND H.DAY = #{day}

mapper.insert:
/*HomeTaxMapper.inputTax*/ UPDATE HOMETAX
INPUTFEE = INPUTFEE + #{inputfee}, RESTFEE = RESTFEE - #{inputfee}, OVERFEE = (INPUTFEE + #{inputfee}) - (RESTFEE - #{inputfee})
WHERE DAY = #{day} AND MYID = #{myid}
WHERE DAY = #{day} AND MYID = (SELECT MYID FROM HOME_MEMBER WHERE NAME = #{name})
select SEQ, OVERFEE FROM HOMETAX
WHERE DAY = #{day} AND MYID = #{myid}
WHERE DAY = #{day} AND MYID = (SELECT MYID FROM HOME_MEMBER WHERE NAME = #{name})
